import Downshift from "downshift";
import { useCallback, useState } from "react";
import classNames from "classnames";
import {
  InputSelectOnChange,
  InputSelectProps,
  InputSelectItem,
} from "./types";

// Define a union type that includes both TItem and the synthetic option
type InputSelectItemExtended<TItem> = TItem | { id: string; firstName: string; lastName: string };

export function InputSelect<TItem>({
  label,
  defaultValue,
  onChange: consumerOnChange,
  items,
  parseItem,
  isLoading,
  loadingLabel,
}: InputSelectProps<TItem>) {
  // Prepend "All Employees" option to the list
  const allEmployeesOption = { id: "all", firstName: "All", lastName: "Employees" };
  const extendedItems = [allEmployeesOption, ...items] as Array<InputSelectItemExtended<TItem>>;

  const [selectedValue, setSelectedValue] = useState<TItem | null>(defaultValue ?? null);

  const onChange = useCallback(
    (selectedItem: InputSelectItemExtended<TItem> | null) => {
      if (!selectedItem) {
        return;
      }

      // If selectedItem is the "All Employees" option, handle it accordingly
      if ((selectedItem as { id: string }).id === "all") {
        // Handle "All Employees" logic here if necessary
        // For example, you might reset to show all items or perform some other action
        consumerOnChange(null); // or whatever logic you need
      } else {
        // Otherwise, handle as a regular TItem
        consumerOnChange(selectedItem as TItem);
      }

      setSelectedValue(selectedItem as TItem);
    },
    [consumerOnChange]
  );

  return (
    <Downshift<InputSelectItemExtended<TItem>>
      id="RampSelect"
      onChange={onChange}
      selectedItem={selectedValue}
      itemToString={(item) => (item ? parseItem(item as TItem).label : "")}
    >
      {({
        getItemProps,
        getLabelProps,
        getMenuProps,
        isOpen,
        highlightedIndex,
        selectedItem,
        getToggleButtonProps,
        inputValue,
      }) => {
        const toggleProps = getToggleButtonProps();

        return (
          <div className="RampInputSelect--root" style={{ position: 'relative' }}>
            <label {...getLabelProps()}>{label}</label>
            <div className="RampBreak--xs" />
            <div
              className="RampInputSelect--input"
              onClick={toggleProps.onClick}
            >
              {inputValue}
            </div>
            <div
              className={classNames("RampInputSelect--dropdown-container", {
                "RampInputSelect--dropdown-container-opened": isOpen,
              })}
              {...getMenuProps()}
              style={{ position: 'absolute', width: '100%', zIndex: 1000 }}
            >
              {renderItems()}
            </div>
          </div>
        );

        function renderItems() {
          if (!isOpen) {
            return null;
          }

          if (isLoading) {
            return <div className="RampInputSelect--dropdown-item">{loadingLabel}...</div>;
          }

          if (extendedItems.length === 0) {
            return <div className="RampInputSelect--dropdown-item">No items</div>;
          }

          return extendedItems.map((item, index) => {
            const parsedItem = item.id === "all"
              ? { label: "All Employees", value: "all" }
              : parseItem(item as TItem);
            return (
              <div
                key={parsedItem.value}
                {...getItemProps({
                  key: parsedItem.value,
                  index,
                  item,
                  className: classNames("RampInputSelect--dropdown-item", {
                    "RampInputSelect--dropdown-item-highlighted": highlightedIndex === index,
                    "RampInputSelect--dropdown-item-selected":
                      selectedItem && parseItem(selectedItem as TItem).value === parsedItem.value,
                  }),
                })}
              >
                {parsedItem.label}
              </div>
            );
          });
        }
      }}
    </Downshift>
  );
}
