import { Fragment, useCallback, useEffect, useMemo, useState } from "react";
import { InputSelect } from "./components/InputSelect";
import { Instructions } from "./components/Instructions";
import { Transactions } from "./components/Transactions";
import { useEmployees } from "./hooks/useEmployees";
import { usePaginatedTransactions } from "./hooks/usePaginatedTransactions";
import { useTransactionsByEmployee } from "./hooks/useTransactionsByEmployee";
import { EMPTY_EMPLOYEE } from "./utils/constants";
import { Employee, Transaction } from "./utils/types";

export function App() {
  const { data: paginatedTransactions, ...paginatedTransactionsUtils } = usePaginatedTransactions();
  const { data: employees, ...employeeUtils } = useEmployees();
  const { data: transactionsByEmployee, ...transactionsByEmployeeUtils } = useTransactionsByEmployee();
  const [isLoading, setIsLoading] = useState(false);
  const [allTransactions, setAllTransactions] = useState<Transaction[]>([]);

  const transactions = useMemo(() => {
    if (transactionsByEmployee !== null) {
      return transactionsByEmployee;
    }
    return allTransactions;
  }, [transactionsByEmployee, allTransactions]);

  const loadAllTransactions = useCallback(async () => {
    setIsLoading(true);
    transactionsByEmployeeUtils.invalidateData();

    await paginatedTransactionsUtils.fetchAll();

    if (paginatedTransactions?.data) {
      setAllTransactions((prevTransactions) => [
        ...prevTransactions,
        ...paginatedTransactions.data,
      ]);
    }

    setIsLoading(false);
  }, [paginatedTransactionsUtils, transactionsByEmployeeUtils, paginatedTransactions]);

  const loadTransactionsByEmployee = useCallback(
    async (employeeId: string) => {
      setAllTransactions([]); // Clear transactions when filtering by employee
      paginatedTransactionsUtils.invalidateData(); // Reset paginated transactions state
      await transactionsByEmployeeUtils.fetchById(employeeId);
    },
    [paginatedTransactionsUtils, transactionsByEmployeeUtils]
  );

  useEffect(() => {
    if (employees === null && !employeeUtils.loading) {
      loadAllTransactions();
    }
  }, [employeeUtils.loading, employees, loadAllTransactions]);

  return (
    <Fragment>
      <main className="MainContainer">
        <Instructions />

        <hr className="RampBreak--l" />

        <InputSelect<Employee>
          isLoading={isLoading}
          defaultValue={EMPTY_EMPLOYEE}
          items={employees === null ? [] : [EMPTY_EMPLOYEE, ...employees]}
          label="Filter by employee"
          loadingLabel="Loading employees"
          parseItem={(item) => ({
            value: item.id,
            label: `${item.firstName} ${item.lastName}`,
          })}
          onChange={async (newValue) => {
            if (newValue === null || newValue === EMPTY_EMPLOYEE) {
              setAllTransactions([]); // Clear transactions when loading all
              paginatedTransactionsUtils.invalidateData(); // Reset pagination state
              await loadAllTransactions();
              return;
            }

            await loadTransactionsByEmployee(newValue.id);
          }}
        />

        <div className="RampBreak--l" />

        <div className="RampGrid">
          <Transactions transactions={transactions} />

          {transactions !== null && (
            <button
              className="RampButton"
              disabled={paginatedTransactionsUtils.loading}
              onClick={async () => {
                await loadAllTransactions();
              }}
            >
              View More
            </button>
          )}
        </div>
      </main>
    </Fragment>
  );
}
